function normalizeCommentData(raw: any[]): CommentNode[] {
    return raw.map((comment) => ({
        commentID: comment.commentID,
        commentContent: comment.commentContent,
        commentDate: comment.commentDate,
        profilePic: comment.profilePic,
        displayName: comment.displayName,
        commentWriterID: comment.commentWriterID,
        thumbsupIDs: comment.thumbsupIDs || [],
        replies: comment.replies?.map((reply: any) => ({
            commentID: reply.commentID,
            commentContent: reply.replyContent,
            commentDate: reply.replyDate,
            profilePic: reply.profilePic,
            displayName: reply.displayName,
            commentWriterID: reply.replyWriterID,
            thumbsupIDs: reply.thumbsupIDs || [], // âœ… preserve thumbs
        })),
    }));
}
