const latestshowReplyToCommentTriggerID = ref(null as any)
const showReplyToCommentTriggerID = ref(null as any)
const showReplyToCommentTrigger = (commentID: number) => {

    showReplyToCommentTriggerID.value = commentID


    if (latestshowReplyToCommentTriggerID.value === commentID) {
        showReplyToCommentTriggerID.value = null
        latestshowReplyToCommentTriggerID.value = null
    } else {
        latestshowReplyToCommentTriggerID.value = commentID
    }
}



const toggleThumbsup = (commentID: number) => {
    const uid = userData().data._id;

    for (const comment of testComment.value) {
        // Top-level comment match
        if (comment.commentID === commentID) {
            const index = comment.thumbsupIDs.indexOf(uid);
            if (index === -1) comment.thumbsupIDs.push(uid);
            else comment.thumbsupIDs.splice(index, 1);
            break;
        }

        // Check replies
        if (comment.replies && Array.isArray(comment.replies)) {
            for (const reply of comment.replies) {
                // Match reply by replyID
                if (reply.commentID === commentID) {
                    if (!reply.thumbsupIDs) reply.thumbsupIDs = [];
                    const index = reply.thumbsupIDs.indexOf(uid);
                    if (index === -1) reply.thumbsupIDs.push(uid);
                    else reply.thumbsupIDs.splice(index, 1);
                    break;
                }
            }
        }
    }

    // âœ… Re-normalize to reflect changes in UI
    testCommentNormalized.value = normalizeCommentData(testComment.value);
};



const handleUploadNewComment = (commentData: any) => {
    const baseComment = {
        commentID: new Date().getTime(),
        commentContent: commentData.commentContent,
        commentDate: new Date().getTime(),
        profilePic: userData().data.profilePic,
        displayName: userData().data.displayName,
        commentWriterID: userData().data._id,
        thumbsupIDs: []
    } as any


    if (commentData.commentsLevel === 1) {
        const newComment = {
            ...baseComment,
            replies: []
        };
        testComment.value.unshift(newComment);

    } else if (commentData.commentsLevel === 2) {

        const newComment = { ...baseComment };
        const targetComment = testComment.value.find(
            (comment: any) => comment.commentID === commentData.rootCommentID
        );
        if (targetComment) {
            targetComment.replies.unshift(newComment);
        }


    } else if (commentData.commentsLevel === 3) {
        const newComment = {
            ...baseComment,
            replyToCommentID: commentData.commentID,
            replyToCommentName: commentData.replyToCommentName
        };
        console.log(newComment)
        const targetComment = testComment.value.find(
            (comment: any) => comment.commentID === commentData.rootCommentID
        );
        if (targetComment) {
            targetComment.replies.push(newComment);
        }
    }
};
const deleteComment = (data: any) => {

    if (data.rootCommentID === null) {
        testComment.value = testComment.value.filter(
            comment => comment.commentID !== data.commentID
        );
    } else if (data.rootCommentID != null) {
        const root = testComment.value.find(comment => comment.commentID === data.rootCommentID);
        if (!root || !root.replies) return;
        root.replies = root.replies.filter(reply => reply.commentID !== data.commentID);
    }

};
