            <Comment :testComment="testComment" @showReplyToCommentTrigger="showReplyToCommentTrigger"
                @toggleThumbsup="toggleThumbsup" @uploadNewComment="handleUploadNewComment"
                @deleteComment="(data) => deleteComment(data)" />

